p {  font-family: Arial, Helvetica, sans-serif; font-size: 12px}
h1 {  font-family: "Times New Roman", Times, serif; font-size: 36px; color: #666699; margin-bottom: 0px; font-weight: normal}
h2 {  font-family: "Times New Roman", Times, serif; font-size: 18px; color: #666699; font-weight: bold; margin-bottom: 0px}
li {  font-family: Arial, Helvetica, sans-serif; font-size: 12px; list-style-type: disc; line-height: normal}
td {  font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal}
ul {  font-family: Arial, Helvetica, sans-serif; font-size: 12px}
h3 {  font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-weight: bold; color: #666699}
a:hover {  text-decoration: underline}
h4 {  font-family: "Times New Roman", Times, serif; font-size: 18px; color: #666699}
a {  text-decoration: none}
caption {  font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-weight: bold}
